import React, { useState } from 'react';
import axios from 'axios';

export default function AiBeastInterface() {
  const [input, setInput] = useState('');
  const [output, setOutput] = useState('');
  const [loading, setLoading] = useState(false);
  const [model, setModel] = useState('meta-llama/llama-3.3-8b-instruct:free');

  const models = [
    "meta-llama/llama-3.3-8b-instruct:free",
    "microsoft/phi-4-reasoning:free",
    "gpt-4o",
    "llama3",
    "claude-3"
  ];

  const apiKeys = {
    "meta-llama/llama-3.3-8b-instruct:free": "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",
    "microsoft/phi-4-reasoning:free": "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",
    "gpt-4o": "YOUR_OPENROUTER_API_KEY",
    "llama3": "YOUR_OPENROUTER_API_KEY",
    "claude-3": "YOUR_OPENROUTER_API_KEY"
  };

  const handleSubmit = async () => {
    setLoading(true);
    try {
      const response = await axios.post(
        'https://openrouter.ai/api/v1/chat/completions',
        {
          model: model,
          messages: [{ role: 'user', content: input }],
        },
        {
          headers: {
            'Authorization': `Bearer ${apiKeys[model] || 'YOUR_OPENROUTER_API_KEY'}`,
            'Content-Type': 'application/json'
          }
        }
      );
      setOutput(response.data.choices[0].message.content);
    } catch (error) {
      setOutput('Error: ' + error.message);
    }
    setLoading(false);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-black to-purple-900 text-white p-8 font-mono">
      <div className="max-w-4xl mx-auto bg-white/10 p-6 rounded-2xl shadow-2xl backdrop-blur-md">
        <h1 className="text-4xl font-bold mb-4 text-center">‚ö° AiBeast Interface</h1>

        <div className="flex gap-4 mb-4">
          <select
            value={model}
            onChange={(e) => setModel(e.target.value)}
            className="bg-black/50 p-2 rounded text-white border border-purple-500"
          >
            {models.map((m) => (
              <option key={m} value={m}>{m}</option>
            ))}
          </select>
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            className="flex-1 bg-black/50 p-2 rounded text-white border border-purple-500"
            placeholder="üß† Ask anything..."
          />
          <button
            onClick={handleSubmit}
            className="bg-purple-700 hover:bg-purple-900 text-white px-4 py-2 rounded shadow-lg"
          >
            {loading ? '‚öôÔ∏è Thinking...' : 'üöÄ Boost'}
          </button>
        </div>

        <div className="mt-4 p-4 bg-black/30 rounded-lg border border-purple-800">
          <pre className="whitespace-pre-wrap text-green-300">
            {output || 'üí¨ Your beast reply will show here...'}
          </pre>
        </div>
      </div>
    </div>
  );
}
